package main.java;import java.lang.*;/*File:	InvalidOperationException.javaAuthor:	amehlhasDate:	March 30, 2018Description: provides error code information*//**Class:	InvalidOperationExceptionDescription: provides error code information*/public class InvalidOperationException extends RuntimeException {            /**         * Constructs a <code>InvalidOperationException</code>          * with no detail message.         */        public InvalidOperationException() {                super();        }        /**         * Constructs a <code>DimensionException</code> with the specified          * detail message.          *         * @param   s   the detail message.         */        public InvalidOperationException(String message) {                super( message );        }}